INSERT INTO actions (period, book_id, title, tip, action_type, is_self, is_draw, is_list, hyperlink) VALUES 
(1, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Call or message someone who has made a positive impact.', 'Reach out to a friend or family member who helped you during a challenging time, and share a specific memory of how their support made a difference. Call or message someone who has made a positive impact.', 'daily', FALSE, FALSE, FALSE, NULL), 
(2, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Write a detailed thank-you note to yourself', 'Include specific achievements or moments of resilience from the past month. what have you done that you’re particularly proud of?', 'daily', TRUE, FALSE, FALSE, NULL), 
(3, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Reflect on an act of kindness you received and pay it forward', 'Think of a time when someone went out of their way for you. Do something similar for a friend, family member, or even a stranger today.', 'daily', FALSE, FALSE, FALSE, NULL), 
(4, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Give a sincere compliment', 'Make your compliment specific. Instead of "you look nice today," try, "your presentation was insightful and really inspired me."', 'daily', FALSE, FALSE, FALSE, NULL), 
(5, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Manifest your goal', 'While meditating, visualize yourself already having achieved your goal. Picture the sights, sounds, and feelings associated with this success. Guided meditation available via qr code.', 'daily', FALSE, FALSE, FALSE, NULL), 
(6, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Take a gratitude walk and take notice of the often-overlooked things all around you', 'Pay attention to details you might usually ignore, like the sound of birds, the texture of leaves, or even the feeling of the ground beneath your feet.', 'daily', FALSE, FALSE, FALSE, NULL), 
(7, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Share your appreciation publicly', 'Post a meaningful thank-you note on social media, or write a handwritten card to someone you’re grateful for and give it to them.', 'daily', FALSE, FALSE, FALSE, NULL), 
(8, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Help someone in need', 'This could be as simple as holding the door open for someone, helping carry groceries, or volunteering your time.', 'daily', FALSE, FALSE, FALSE, NULL), 
(9, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Donate to a cause you care about', 'It doesn’t have to be a large amount. Even a small donation or volunteering for a few hours can make a difference.', 'daily', FALSE, FALSE, FALSE, NULL), 
(10, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Send flowers to someone you love', 'If sending flowers isn’t feasible, consider sending a thoughtful message or small token of appreciation instead.', 'daily', FALSE, FALSE, FALSE, NULL), 
(11, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Cook a meal for someone', 'This could be a friend, family member, or neighbor. It doesn’t have to be elaborate; it’s the gesture that counts.', 'daily', FALSE, FALSE, FALSE, NULL), 
(12, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Share a positive memory with someone', 'Reach out to a friend or loved one and reminisce about a fun or meaningful experience you’ve shared together.', 'daily', FALSE, FALSE, FALSE, NULL), 
(13, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Mentor or teach someone', 'Share a skill you’re good at or provide helpful advice to someone who could benefit from your experience.', 'daily', FALSE, FALSE, FALSE, NULL), 
(14, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Spend Time In Nature - Go On A Hike Or Long Walk In The Park', 'Leave your phone behind (or in your pocket) and focus on your surroundings. Notice the details that make nature beautiful.', 'daily', FALSE, FALSE, FALSE, NULL), 
(15, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Create A “Gratitude Jar” And Add A Note Every Day This Week', 'Write a short note about something you’re grateful for each day. At the end of the week, read the notes to reflect on the positives in your life.', 'daily', FALSE, FALSE, FALSE, NULL), 
(16, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Write down top 5 wins you want to recreate', 'Reflect on previous accomplishments that made you feel proud. Identify what made those successes possible.', 'daily', FALSE, FALSE, FALSE, NULL), 
(17, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Thank a service worker or mentor', 'Acknowledge their efforts with a kind word or a thank-you card. Explain how their work or guidance has made a difference in your life.', 'daily', FALSE, FALSE, FALSE, NULL), 
(18, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Leave a positive review', 'Write a review for a business or service you’ve appreciated recently. Be specific about what made your experience great.', 'daily', FALSE, FALSE, FALSE, NULL), 
(19, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Meditate on gratitude', 'During meditation, focus on three things you’re deeply grateful for. Feel the emotions associated with each one and let them fill you up.', 'daily', FALSE, FALSE, FALSE, NULL), 
(20, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Do something nice for yourself today', 'Treat yourself to a relaxing activity, buy a small gift, or set aside time to do something you love.', 'daily', FALSE, FALSE, FALSE, NULL), 
(21, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Practice gratitude in nature', 'Sit outside, take a few deep breaths, and think of three things in your surroundings that bring you peace or joy.', 'daily', FALSE, FALSE, FALSE, NULL), 
(22, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Make a list of people you are grateful for', 'Reflect on who has had a positive impact in your life.', 'daily', FALSE, FALSE, FALSE, NULL), 
(23, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Send a message or call each person on your gratitude list', 'Don’t procrastinate; tomorrow is not guaranteed.', 'daily', FALSE, FALSE, FALSE, NULL), 
(24, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Give Someone Praise And Encouragement', 'Reflect on a friend or family member who may be struggling right now. Reach out to them to express your support & encouragement.', 'daily', FALSE, FALSE, FALSE, NULL), 
(25, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Share a personal success story to inspire others', 'Reflect on a time when you overcame a challenge. Share your story with someone who could benefit from hearing it.', 'daily', FALSE, FALSE, FALSE, NULL), 
(26, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Gratitude Art', 'Draw something that inspires you. (Draw Something That Inspires You)', 'daily', FALSE, TRUE, FALSE, NULL), 
(27, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Write A Review Or Testimonial For Someone Who Provides A Service.', 'Think of a local business owner, service provider, or someone you know who would appreciate a public acknowledgment of their work.', 'daily', FALSE, FALSE, FALSE, NULL), 
(28, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Practice Self-Compassion By Writing A Letter Of Forgiveness To Yourself', 'Address any feelings of guilt or self-criticism. Remind yourself that you’re human, learning, and growing.', 'daily', TRUE, FALSE, FALSE, NULL), 
(29, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Celebrate someone’s success', 'Reflect on someone with a recent accomplishment you admire. Celebrate with them in a meaningful way.', 'daily', FALSE, FALSE, FALSE, NULL), 
(30, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Reflect on your blessings', 'List the things you are most grateful for today.', 'daily', FALSE, FALSE, TRUE, NULL), 
(31, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Reconnect With An Old Friend', 'Connect with a friend you shared a meaningful connection in life and express your gratitude for them.', 'daily', FALSE, FALSE, FALSE, NULL), 
(32, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Take action, don’t over think it, even the smallest of surprises makes a meaningful difference', 'Take action, don’t over think it, even the smallest of surprises makes a meaningful difference.', 'daily', FALSE, FALSE, FALSE, NULL), 
(33, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Send A Gratitude Letter To A Mentor Or Teacher', 'Thank them for the knowledge and wisdom they’ve imparted, and mention specific ways they’ve influenced your life.', 'daily', FALSE, FALSE, FALSE, NULL), 
(34, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Express Gratitude To A Partner', 'Take a moment to reflect on the moments that have inspired joy with them.', 'daily', FALSE, FALSE, FALSE, NULL), 
(35, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Reflect On A Challenging Experience That Made You Stronger.', 'Write about how you grew from it and the lessons you learned. Gratitude can be found even in tough times.', 'daily', TRUE, FALSE, FALSE, NULL), 
(36, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Support A Local Business', 'Reflect on a business you have a meaningful connection with, and provide them with support.', 'daily', FALSE, FALSE, FALSE, NULL), 
(37, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Make One Small Gesture To Brighten Someone’s Day', 'A compliment, a helping hand, or a listening ear. TRUE success is measured by the impact we leave in others’ lives.', 'daily', FALSE, FALSE, FALSE, NULL), 
(38, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Gift A Book That Has Impacted You To Someone Who Might Benefit From It', 'Choose a book that has inspired or helped you, and give it to someone who could benefit from its message.', 'daily', FALSE, FALSE, FALSE, NULL), 
(39, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Create A Gratitude Playlist', 'Start your gratitude playlist with songs that bring you joy and peace. Choose songs that make you feel uplifted, remind you of happy memories, or inspire thankfulness for the little things. Mix in songs with lyrics that celebrate life, love, and connection to keep the vibe positive and reflective.', 'daily', FALSE, FALSE, FALSE, NULL), 
(40, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Create One Boundary You Can Gently Reinforce Or Establish', 'Whether in personal time, space, or energy. Remember, healthy boundaries nurture respect and strengthen connections.', 'daily', FALSE, FALSE, FALSE, NULL), 
(41, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Express Gratitude To Your Parents', 'At the least they brought you into this world.', 'daily', FALSE, FALSE, FALSE, NULL), 
(42, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Reach Out And Share Some Encouragement With Someone', 'Whether it’s a simple compliment, a reminder of their strengths, or an acknowledgment of their hard work, a few kind words can make a big difference. Sometimes, the support we give to others is exactly what they need to keep going.', 'daily', FALSE, FALSE, FALSE, NULL), 
(43, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Do A Random Act Of Kindness For A Stranger', 'Look for simple, spontaneous ways to brighten a stranger’s day. Offer a compliment, hold the door, let someone go ahead in line, or pick up the tab for a coffee. Small acts can create ripples of kindness that last far beyond the moment.', 'daily', FALSE, FALSE, FALSE, NULL), 
(44, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Host A Virtual Gratitude Gathering', ' Look for simple, spontaneous ways to brighten a stranger’s day. Offer a compliment, hold the door, let someone go ahead in line, or pick up the tab for a coffee. Small acts can create ripples of kindness that last far beyond the moment.', 'daily', FALSE, FALSE, FALSE, NULL), 
(45, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Write a letter of appreciation to your future self', 'Imagine where you want to be in a year, and express gratitude for the steps you’re taking now to get there. Include words of encouragement for the journey ahead.', 'daily', TRUE, FALSE, FALSE, NULL), 
(1, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Create a daily wellness ritual', 'Identify a wellness ritual that resonates with you and commit to practicing it throughout the week.', 'weekly', FALSE, FALSE, FALSE, 'https://vimeo.com/1049898817/2c88b4fed6'), 
(2, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Dedicate 10 minutes each day to mindful reflection or meditation.', 'Focus on your breath and bring awareness to your thoughts and feelings.', 'weekly', FALSE, FALSE, FALSE, 'https://vimeo.com/1049899348/b3fcdbd12f'), 
(3, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Write your eulogy: discover the legacy you want to leave behind', 'This week, take a deeper look at your life’s journey. The “write your eulogy” exercise is not about focusing on death—it’s a powerful reflection on how you want to live and be remembered. Writing your own eulogy is an opportunity to gain clarity on what truly matters to you, align your actions with your deepest values, and motivate yourself to live with purpose and intention. Imagine that you are looking back on a long, fulfilling life. What impact did you make? What kind of person were you? This exercise will help you discover the legacy you want to create.', 'weekly', FALSE, FALSE, FALSE, 'https://vimeo.com/1049900053/49801bf3b9'), 
(4, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Reach out to an old friend or family member and reconnect by expressing your appreciation for them', 'In person or via facetime/zoom.', 'weekly', FALSE, FALSE, FALSE, 'https://vimeo.com/1049900615/60c59eec6b'), 
(5, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Define the vision for your life and complete the my journey pages.', '', 'weekly', FALSE, FALSE, FALSE, 'https://vimeo.com/1049900980/02bd871389'), 
(6, '7215727d-cefa-460e-a5a0-478ec1002d08', 'Identify a person who has positively impacted your life and write them a detailed thank-you letter.', 'Deliver it in person or through a handwritten note.', 'weekly', FALSE, FALSE, FALSE, NULL);
